from sympy import prevprime, primepi
def A377994(n):
    def f(x):
        if x<=3: return n
        p = prevprime(x+1)
        i = int(primepi(p))
        return n+(p>>1)+(x-p-((i^x)&1)>>1)
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
    return m # _Chai Wah Wu_, Nov 13 2024

