# OEIS sequence: A378035

from sympy import mobius, integer_nthroot, prime
def A378035(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return int(x-1+sum(mobius(k)*(integer_nthroot(x,k)[0]-1) for k in range(2,x.bit_length())))
    m = (p:=prime(n)-1)-f(p)
    return bisection(lambda x:f(x)+m,m,m) # _Chai Wah Wu_, Nov 25 2024

