from math import factorial
def a(n):
    total = sum(factorial(n) // (factorial(n - 2 * k) * 2**k * factorial(k))
                for k in range(n // 2 + 1))
    deranged = factorial(n) // (2**(n // 2) * factorial(n // 2)) if n % 2 == 0 else 0
    return total - deranged
