from math import comb, factorial
def A380129(n):
    def p(m): return sum((-1 if i&1 else 1)*comb(m,i)*comb(n,i)*factorial(i)*(n-i)**(m-i) for i in range(m+1))<<1
    kmin, kmax = n, n+1
    while p(kmax) < n**kmax: kmax<<=1
    while kmax-kmin > 1:
        kmid = kmax+kmin>>1
        if p(kmid) >= n**kmid:
            kmax = kmid
        else:
            kmin = kmid
    return kmax # _Chai Wah Wu_, Jan 21 2025

