from num2words import num2words as n2w
def f(n): return sum(1 for c in n2w(n).replace(" and", "") if c.isalpha())
def row(n):
    c, i, J = 1, 0, list(range(1, n+1))
    out = []
    while len(J) > 1:
        i = (i + f(c))%len(J)
        q = J.pop(i)
        out.append(q)
        c = c+1
    out.append(J[0])
    return out
print([e for n in range(1, 15) for e in row(n)]) # _Michael S. Branicky_, Feb 15 2025

