from sympy import isprime, nextprime
def a(n):
    p, b = 2, 3**n+1
    while not isprime(p+b):
        p = nextprime(p)
    return p
print([a(n) for n in range(65)]) # _Michael S. Branicky_, Apr 23 2025
from sympy import nextprime, isprime
def A381041(n):
    p = 3**n+1
    q = nextprime(p)
    while not isprime(q-p):
        q = nextprime(q)
    return q-p # _Chai Wah Wu_, May 01 2025

