# OEIS sequence: A381736

from math import isqrt
from sympy import primepi, primerange, integer_nthroot
def A381736(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        kmin = kmax >> 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return n+x-sum(primepi(min(x//(p*q),p*q-1))-b for a,p in enumerate(primerange(integer_nthroot(x,3)[0]+1),1) for b,q in enumerate(primerange(p+1,isqrt(x//p)+1),a+1))
    return bisection(f,n,n) # _Chai Wah Wu_, Mar 28 2025

