# OEIS sequence: A381852

def a(n):
    b = bin(n)[2:]
    zi = b.find('0')
    return n if zi == -1 else int(b[:zi+1]+"".join('0' if bi == '1' else '1' for bi in b[zi+1:]), 2)
print([a(n) for n in range(70)]) # _Michael S. Branicky_, Mar 09 2025
