# OEIS sequence: A381869

from sympy import nextprime, prime, sieve
def a(n):
    plst = list(sieve.primerange(3, prime(2*n+1)+1))
    s = sum(plst)
    while s%10:
        q = nextprime(plst[-1])
        s += (q-plst[0])
        plst = plst[1:] + [q]
    return plst[0]
print([a(n) for n in range(1, 74)]) # _Michael S. Branicky_, Mar 09 2025

