def ok(n):
    c = 0
    while n and n%7 == 0: n //= 7; c += 1
    return c&1 == 0
print([k for k in range(1, 86) if ok(k)]) # _Michael S. Branicky_, Apr 04 2025
from sympy import integer_log
def A382745(n):
    def f(x): return n+x-sum((k:=x//7**m)-k//7 for m in range(0,integer_log(x,7)[0]+1,2))
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
