# OEIS sequence: A001690

def f(n):
    a=1
    b=2
    c=3
    while n>0:
        a=b
        b=c
        c=a+b
        n-=(c-b-1)
    n+=(c-b-1)
    return (b+n)
for i in range(1,1001):
    print(str(i)+" "+str(f(i))) # _Indranil Ghosh_, Dec 22 2016

