# OEIS sequence: A002559

markov = set[tuple[int, int, int]]
def MarkovNumbers(len: int = 50, MAX: int = 10**10) -> list[int]:
    cur: markov = {(1, 1, 1), (1, 1, 2), }
    def step(triples: markov) -> markov:
        ret: markov = set()
        for (a, b, c) in triples:
            for k in (1, 2, 0):
                x, y, z = (a, b, c) if k == 0 else (a, b, c)[k:] + (a, b, c)[:k]
                t = tuple(sorted((x, y, 3 * x * y - z)))
                if max(t) < MAX: ret.add(t)
        return ret
    while True:
        new = step(cur)
        if new == cur: break
        cur = new
    return sorted({n for triple in cur for n in triple})[:len]
print(MarkovNumbers(len=42))  # _Peter Luschny_, Aug 10 2025

