from sympy import nextprime
def a_list(upto: int) -> list[int]:
    L: list[int] = [0]
    count = 1; bn = 1; bd = 1; p = 2
    for k in range(1, upto + 1):
        bn *= p
        bd *= p - 1
        while bn > count * bd:
            L.append(k)
            count += 1
        p = nextprime(p)
    return L
print(a_list(1000))  # _Chai Wah Wu_, Apr 17 2015, adapted by _Peter Luschny_, Jan 25 2025

