# OEIS sequence: A006530

# a(0) = 0, a(1) = 1, gpf for n >= 2.
def gpf_list(n: int) -> list[int]:
    L = [0] * (n + 1); L[1] = 1
    for p in range(2, n + 1):
        if L[p] == 0:  # p is prime
            for k in range(p, n + 1, p):
                L[k] = p
    return L
