from gmpy2 import digits
def A032937(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def g(x):
        s = digits(x,4)
        for i in range(l:=len(s)):
            if s[i]>'1':
                break
        else:
            return int(s,2)
        return int(s[:i]+'1'*(l-i),2)
