from itertools import count, islice, product
def ok(n): return int(max(str(n))) < 2 and str(n) in bin(n)
def agen(): # generator of terms
    yield 0
    for d in count(1):
        for rest in product("01", repeat=d-1):
            k = int("1" + "".join(rest))
            if ok(k):
                yield k
