# OEIS sequence: A046389

from math import isqrt
from sympy import primepi, integer_nthroot, primerange
def A046389(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return int(n+x-sum(primepi(x//(k*m))-b for a,k in enumerate(primerange(3,integer_nthroot(x,3)[0]+1),2) for b,m in enumerate(primerange(k+1,isqrt(x//k)+1),a+1)))
    return bisection(f,n,n) # _Chai Wah Wu_, Sep 10 2024

