from sympy import isprime
from itertools import count, islice, product
def t(s): return s.translate({ord("2"):ord("5"), ord("5"):ord("2")})
def a(n):
    if n < 2: return 0
    c = 0
    for mid in product("01258", repeat=n-2):
        s = "1" + "".join(mid) + "1"
        ss = set([s, s[::-1], t(s), t(s[::-1])])
