# OEIS sequence: A051634

from sympy import nextprime
def aupto(limit):
    alst, p, q, r = [], 2, 3, 5
    while q <= limit:
        if 2*q > p + r: alst.append(q)
        p, q, r = q, r, nextprime(r)
    return alst
