# OEIS sequence: A053547

from sympy import isprime
def A053547(n):
    s = int(''.join(str(m) for m in range(n,0,-1)))
    for i in range(1,10):
        s *= 10
        for j in range(1,10**i,2):
            x = s+j
            if isprime(x):
                return x
    else:
        return 'search limit reached.' # _Chai Wah Wu_, Jan 02 2015

