# OEIS sequence: A053707

from sympy import primepi, integer_nthroot
def A053707(n):
    if n==1: return 3
    def f(x): return int(n-2+x-sum(primepi(integer_nthroot(x,k)[0]) for k in range(2,x.bit_length())))
    kmin, kmax = 1,2
    while f(kmax)+1 >= kmax:
        kmax <<= 1
    rmin, rmax = 1, kmax
    while True:
        kmid = kmax+kmin>>1
        if f(kmid)+1 < kmid:
            kmax = kmid
        else:
            kmin = kmid
        if kmax-kmin <= 1:
            break
    while True:
        rmid = rmax+rmin>>1
        if f(rmid) < rmid:
            rmax = rmid
        else:
            rmin = rmid
        if rmax-rmin <= 1:
            break
    return kmax-rmax # _Chai Wah Wu_, Aug 13 2024

