from itertools import count, islice, product
def agen(): # generator of terms
    yield from [0, 1]
    for d in count(2):
        for rest in product("01", repeat=d//2-1):
            left = "1" + "".join(rest)
            for mid in [[""], ["0", "1"]][d%2]:
                yield int(left + mid + left[::-1])
