# OEIS sequence: A058049

from sympy import isprime, nextprime
def sd(n): return sum(map(int, str(n)))
def aupto(limit):
    alst, k, p, s = [], 1, 2, 2
    while k <= limit:
        if isprime(s): alst.append(k)
        k += 1; p = nextprime(p); s += sd(p)
    return alst
