# OEIS sequence: A060445

def a(n):
    if n<1: return 0
    n=2*n + 1
    N=n
    x=0
    while True:
        if n%2==0: n//=2
        else: n = 3*n + 1
        x+=1
        if n<N: break
    return x
print([a(n) for n in range(101)]) # _Indranil Ghosh_, Apr 22 2017

