# OEIS sequence: A060846

from sympy import primepi, integer_nthroot, nextprime
def A060846(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return int(n+x-sum(primepi(integer_nthroot(x,k)[0]) for k in range(2,x.bit_length())))
    return nextprime(bisection(f,n,n)) # _Chai Wah Wu_, Sep 15 2024

