# OEIS sequence: A060847

from sympy import primepi, integer_nthroot, prevprime
def A060847(n):
    def f(x): return int(n+x-sum(primepi(integer_nthroot(x,k)[0]) for k in range(2,x.bit_length())))
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    return (a:=bisection(f,n,n))-prevprime(a) # _Chai Wah Wu_, Sep 13 2024

