from itertools import count, islice
def agen(): # generator of terms
    N, i = [], 1
    for n in count(1):
        target = n*(n-1)//2
        while len(N) <= target+n+1:
            N.extend(list(str(i**2)))
            i += 1
        yield int("".join(N[target:target+n]))
