from sympy import isprime, prime
def a(n):
    pn = prime(n); k = 1
    while not isprime(prime(k*n) - k*pn): k += 1
    return k
print([a(n) for n in range(1, 98)]) # _Michael S. Branicky_, Jul 04 2021

