# OEIS sequence: A072829

from math import factorial, comb
def A072829(n):
    f = factorial(n)
    def p(m): return comb(m,n)*f<<1
    kmin, kmax = n-1, n
    while p(kmax) <= kmax**n: kmax<<=1
    while kmax-kmin > 1:
        kmid = kmax+kmin>>1
        if p(kmid) > kmid**n:
            kmax = kmid
        else:
            kmin = kmid
    return kmin # _Chai Wah Wu_, Jan 21 2025

