from gmpy2 import digits
def A074940(n):
    def f(x):
        s = digits(x,3)
        for i in range(l:=len(s)):
            if s[i]>'1':
                break
        else:
            return n+int(s,2)
        return n+int(s[:i]+'1'*(l-i),2)
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
