# OEIS sequence: A075774

def A075774(n):
    t = [(10**i, 2) for i in [12, 9, 6, 3, 2]]
    if n > 99:
        for ti, sti in t:
            if n >= ti:
                q, r = divmod(n, ti)
                return A075774(q) + sti + (A075774(r) if r else 0)
    if n < 20:
        return 1 + (n in {0, 7, 13, 14, 15, 16, 18, 19}) + 2*(n in {11, 17})
    else: return 2 + (n//10==7) + (A075774(n%10) if n%10 else 0)
