from math import isqrt
from sympy.ntheory.primetest import is_square
def aupton(terms):
    alst = [7]
    for n in range(2, terms+1, 2):
        sq1, an = alst[-1]**2, alst[-1] + 1
        while not is_square(sq1 + an**2): an += 1
        alst.extend([an, isqrt(sq1 + an**2)])
    return alst[:terms]
