# OEIS sequence: A082007

from math import log
def A082007( n ):
  if n == 0: return 0
  y = 2 ** int( log( n + 1, 2 ) )
  yc = 2 ** 2 ** int( log( log( y, 2 ), 2 ) )
  yr = y / yc
  return (yc-1) * int( (n+1-y)/yr + 1 ) + A082007( yr + (n+1)%yr - 1 )
