from sympy import primepi, integer_nthroot
def A083390(n):
    def f(x): return int(n+sum(primepi(integer_nthroot((x<<1)|1,k)[0])-1 for k in range(1,x.bit_length()+1)))
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
