from math import isqrt
from itertools import count, islice, permutations
def agen(verbose=False): # generator of terms
    adict, n, seen = dict(), 2, set()
    for m in count(10):
        t = str(m**2)
        k = "".join(sorted(t))
        if k in seen: continue
        seen.add(k)
        V = set(r for p in permutations(t) if p[0]!='0' and (r:=isqrt(k:=int("".join(p))))**2 == k)
        if len(V) > 0 and len(V) not in adict:
            if verbose: print("FOUND", len(V), m)
            adict[len(V)] = m
            while n in adict: yield adict[n]; n += 1
print(list(islice(agen(), 10))) # _Michael S. Branicky_, Oct 07 2025

