from sympy import factorint, primepi
def a(n):
    if n == 1: return 1
    return int("".join(str(primepi(p))*e for p, e in factorint(n).items()))
print([a(n) for n in range(1, 62)]) # _Michael S. Branicky_, Oct 01 2024

