# OEIS sequence: A088639

from sympy import isprime
def a(n):
  if n == 0: return 2
  nns, i, pow10 = int(str(n)*n), 1, 1
  while True:
    i = 1
    while i < pow10:
      t = nns * pow10 + i
      if isprime(t): return t
      i += 2
    pow10 *= 10
print([a(n) for n in range(16)]) # _Michael S. Branicky_, Feb 11 2021

