from sympy import isprime
from itertools import combinations, count, islice
def agen(): # generator of terms
    for d in count(2):
        b = (1<<(d+2))-1
        for i, j in combinations(range(d), 2):
            if isprime(t:=b-(1<<(d-i))-(1<<(d-j))):
                yield t
