from sympy import isprime
def ok(n, prevset):
    if not isprime(n): return False
    s = str(n)
    si = (s[:i]+s[i+1:] for i in range(len(s)))
    return any(t[0] != '0' and int(t) in prevset for t in si)
def afind(terms):
    s, snxt = {2, 3, 5, 7}, set()
    print(len(s), end=", ")
    for n in range(2, terms+1):
        for i in range(10**(n-1), 10**n):
            if ok(i, s):
                snxt.add(i)
        s, snxt = snxt, set()
        print(len(s), end=", ")
