from math import gcd
from itertools import islice
def agen(): # generator of terms
    an, aset, mink = 1, {1}, 2
    while True:
        yield an
        k = mink
        while k in aset or gcd(an, k) != 1 or abs(k-an) == 1: k += 1
        an = k
        aset.add(an)
        while mink in aset: mink += 1
