from sympy import isprime, nextprime
def ispal(n): s = str(n); return s == s[::-1]
def aupto(lim):
  n, p, alst = 1, 2, []
  while p <= lim:
    if isprime(int(str(n)+str(p))): n, alst = n + 1, alst + [p]
    p = nextprime(p)
  return alst
