from math import factorial
from gmpy2 import is_prime
from itertools import count, islice
def agen():
    for n in count(1):
        f = factorial(n)
        yield from (t for i in range(1, n+1) if is_prime(t:=f*i+1))
print(list(islice(agen(), 32))) # _Michael S. Branicky_, Apr 30 2025

