def q3ub(n):
    sum = 0;
    vlm = 2**n; # 2 to the n-th power
    combi = 1; # combinatorial coefficient (n k)
    for k in range(n+1): # for k := 0 to n
        c = min(combi, vlm);
        sum = sum + c;
        vlm = vlm - c;
        vlm = vlm // 2; # integer division, result is truncated
        combi = (combi * (n-k)) // (k+1) # division is exact
    #end for k
    return sum

