from math import isqrt
from sympy import integer_nthroot
def A125643(n):
    if n <= 4: return n-1>>1
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return n-2+x-integer_nthroot(x,3)[0]-isqrt(x)
    return bisection(f,n-2,n-2) # _Chai Wah Wu_, Oct 14 2024

