# OEIS sequence: A131849

from networkx import empty_graph, find_cliques
from itertools import combinations
from sympy import isprime
def A131849(n):
    if n == 0: return 0
    v = list(range(1,n+1))
    G = empty_graph(v)
    G.add_edges_from((a,b) for a, b in combinations(v,2) if not isprime(abs(a-b)+1))
    return max(len(c) for c in find_cliques(G)) # _Chai Wah Wu_, Jan 09 2026

