from sympy import nextprime
from itertools import islice
def agen(): # generator of terms
    pn, primk, pk, pkplus = 2, 2, 2, 3
    while True:
        while primk * pkplus <= pn:
            primk, pk, pkplus = primk*pkplus, pkplus, nextprime(pkplus)
