# OEIS sequence: A140488

def aupton(terms):
    alst = [5]
    while len(alst) < terms:
        an, k, smallest = alst[-1], 2, False
        while not smallest or an%k == 0:
            if not smallest and an%k != 0: smallest = True
            k += 1
        alst.append(an+k)
    return alst
