# OEIS sequence: A141242

from sympy import primepi, integer_nthroot, primerange, factorint
def A141242(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        kmin = kmax >> 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return int(n+x-sum(primepi(integer_nthroot(x,k-1)[0]) for k in primerange(x.bit_length()+1)))
    return list(factorint(bisection(f,n,n)).values())[0]+1 # _Chai Wah Wu_, Feb 22 2025

