def msb(n):
    t=0
    while n>>t>0: t+=1
    return 2**(t - 1)
def a139250(n):
    k=(2*msb(n)**2 + 1)//3
    return 0 if n==0 else k if n==msb(n) else k + 2*a139250(n - msb(n)) + a139250(n - msb(n) + 1) - 1
def a(n): return 0 if n==0 else (a139250(n + 2) - 3)//4
