from Levenshtein import distance  # after pip install python-Levenshtein
from sympy import nextprime
def a(n):
    m = 0
    while True:
        b = bin(m)[2:]
        if distance(b, bin(nextprime(m-1))[2:]) == n:
            return m
        m += 1
print([a(n) for n in range(1, 16)]) # _Michael S. Branicky_, Feb 05 2022

